import { TableColumnOptions } from "typeorm/schema-builder/options/TableColumnOptions.js";
import { TableIndexOptions } from "typeorm/schema-builder/options/TableIndexOptions.js";
import { TableForeignKeyOptions } from "typeorm/schema-builder/options/TableForeignKeyOptions.js";
import { TableUniqueOptions } from "typeorm/schema-builder/options/TableUniqueOptions.js";
import { TableCheckOptions } from "typeorm/schema-builder/options/TableCheckOptions.js";
import { TableExclusionOptions } from "typeorm/schema-builder/options/TableExclusionOptions.js";
/**
 * Table options.
 */
export interface TableOptions {
    /**
     * Table schema.
     */
    schema?: string;
    /**
     * Table database.
     */
    database?: string;
    /**
     * Table name.
     */
    name: string;
    /**
     * Table columns.
     */
    columns?: TableColumnOptions[];
    /**
     * Table indices.
     */
    indices?: TableIndexOptions[];
    /**
     * Table foreign keys.
     */
    foreignKeys?: TableForeignKeyOptions[];
    /**
     * Table unique constraints.
     */
    uniques?: TableUniqueOptions[];
    /**
     * Table check constraints.
     */
    checks?: TableCheckOptions[];
    /**
     * Table check constraints.
     */
    exclusions?: TableExclusionOptions[];
    /**
     * Indicates if table was just created.
     * This is needed, for example to check if we need to skip primary keys creation
     * for new tables.
     */
    justCreated?: boolean;
    /**
     * Enables Sqlite "WITHOUT ROWID" modifier for the "CREATE TABLE" statement
     */
    withoutRowid?: boolean;
    /**
     * Table engine.
     */
    engine?: string;
    /**
     * Table comment. Not supported by all database types.
     */
    comment?: string;
}
